require_relative "../helpers"
require "pry"

# https://adventofcode.com/2022/day/6
include Helpers

class Advent
  def data
    @data ||= DATA.read.split("\n").map(&:chars)
  end

  def part_one
    data.map { |line|
      i = 0
      while i < line.length
        # 0..3
        # 1..4
        if line[i..(i + 3)].uniq.length == line[i..(i + 3)].length
          return i + 4
        else
          i += 1
        end
      end
    }
  end

  def part_two
    data.map { |line|
      i = 0
      while i < line.length
        # 0..13
        # 1..14
        if line[i..(i + 13)].uniq.length == line[i..(i + 13)].length
          return i + 14
        else
          i += 1
        end
      end
    }
  end
end

if $0 == __FILE__
  advent = Advent.new
  assert_equal advent.part_one, 1544
  assert_equal advent.part_two, 2145
end

__END__

